import React from "react";
import ReactDOM from 'react-dom';
import './res/styles/main.css';
import {Content} from "./components/content";
import PopupHandler from "./components/popup-handler";
import {DrawBoxComponent} from "./contents/git/git-components";
import {Circle} from "./components/drawbox/simple/circle";
import {Rectangle} from "./components/drawbox/simple/rectangle";
import {Utils} from "./tools/utils";
import {Line2} from "./components/drawbox/simple/line2";
import Dot from "./components/drawbox/simple/dot";

function getMainContent() {
    return <><Content/><PopupHandler/></>;
}

function getScript() {
    let p1 = [100, 150];
    let p2 = [90, 300];
    let p3 = [210, 223];
    let p4 = ['65d', 100];
    let p5 = ['190d', 150];

    let c1 = new Circle()
        .setPosition([150, 200])
        .setRadius(50)
        .setStrokeWidth(1);

    let c2 = new Circle()
        .setPosition([300, 400])
        .setRadius(20)
        .setStrokeWidth(1);

    let l1 = new Line2()
        .setStart(p1)
        .setQuadraticPoint([c2, 5])
        .setEnd(p2)

    //-2.2455372690184494
    //-2.6971734436886945
    //-2.471355356353572

    let p6 = new Dot().setPosition(c2.getExternalPoint(-2.2455372690184494, 5));
    let p7 = new Dot().setPosition(c2.getExternalPoint(-2.6971734436886945, 5));
    let p8 = new Dot().setPosition(c2.getExternalPoint(-2.471355356353572, 5));
    let l2 = new Line2().setStart(p1).setEnd(p6);
    let l3 = new Line2().setStart(p2).setEnd(p8);

    Utils.db
        .addShape(p6)
        .addShape(p7)
        .addShape(p8)
        .addShape(l2)
        .addShape(l3)
        .addShape(c2)
        .addShape(l1)

    return (
        <DrawBoxComponent wh={[500, 500]} shapeFactory={Utils.db}/>
    )
}

// ReactDOM.render(getMainContent(), document.querySelector('#root'));
ReactDOM.render(getScript(), document.querySelector('#root'));
