import {Shape} from "./shape";
import {Line} from "./line";

export class Path extends Shape {
    points = [];

    constructor() {
        super();
        this.setStrokeWidth(1);
        this.offset2 = [0, 0];
    }

    /**
     * @param point [x,y]
     */
    setStart(point) {
        this.points[0] = point;
        return this;
    }

    /**
     * NOTICE TO KEEP AN SPACE BETWEEN EACH INPUT<br/>
     *
     * ox and oy are optional<br/>
     * ox: offset x<br/>
     * oy: offset y<br/><br/>
     *
     * input format:<br/>
     *
     * a pair of [x, y]:<br/>
     *      [num, num] for exact location. ex: 12.5, 300<br/>
     *      [numR, length] for radian, [numD, length] for degree. ex: [180d, 100] or [3.1415r, 100]<br/>
     *      [shape, gap] for reference to a shape. ex: [new Circle()..., 10]<br/>
     *
     * a pair of [ox, oy]: [num, num]. ex: [15, 30]<br/>
     *
     * @param path 'm x y [ox oy]':moveTo(x, y, ox, oy), 'l x y [ox oy]':lineTo(x, y, ox, oy), 'q cx cy x y [ox oy]':quadraticTo(curveX, curveY, x, y, ox, oy)
     */
    path(path) {
        let arr = path.split('/ +/g');
        let l = arr.length;
        let mem = null;
        let offset = null;
        let t1, t2;
        for (let i = 0; i < l; i++) {
            switch (arr[i]) {
                case 'm':
                    mem = {p: [arr[i + 1], arr[i + 2]]};
                    i += 2;
                    break;
                case 'l':
                    t1 = new Line().setEnd(arr[i + 1], arr[i + 2]);
                    if (mem != null) {
                        t1.setStart(mem.p);
                        t1.setOffset(mem.offset);
                    }
                    this.points.push({line: t1, len: t1.getLength()})
                    i += 2;
                    break;
                case 'q':
                    break;
                default:
                    throw new Error('Wrong input')
            }
        }
    }

    /**
     * @param point [x,y] or [intR, length] for radian, [intD, length] for degree or [shape,gap].<br/>
     */
    getLineTo(start, end, offset1 = [0, 0], offset2 = [0, 0]) {
        return new Line().setStart(start).setEnd(end).setOffset(offset1).setOffset2(offset2)
    }

    /**
     * @param curve [x,y] or [intR, length] for radian, [intD, length] for degree or [shape,gap].<br/>
     * @param end [x,y] or [intR, length] for radian, [intD, length] for degree or [shape,gap].<br/>
     */
    getQuadraticTo(start, curve, end, offset1 = [0, 0], offset2 = [0, 0]) {
        new Line().setStart(start).setQuadraticPoint(curve).setEnd(end).setOffset(offset1).setOffset2(offset2)
    }

    /**
     * show arrows on the line
     * @param direction 0 or 1
     * @param places array of float values, each is 0 - 1;
     * @return Shape
     */
    showArrow(direction, places) {
        this.direction = direction;
        this.places = typeof places === 'number' ? [places] : places;
        return this;
    }

    render(canvasCtx) {
        this.prepareLines();
        for (let i = 1; i < this.points.length; i++) {
            this.points[i].render(canvasCtx);
        }
    }

    prepareLines() {
        let lengths = [0]; // extract each line length to determine exact place of arrow

        //prepare lines. connect lines to each other
        this.points[1].setOffset(this.offset);
        this.points[this.points.length - 1].setOffset2(this.offset2);

        this.points[1].setStart(this.points[0]);
        this.initLine(this.points[1]);
        lengths.push(this.points[1].getLength());

        for (let i = 2; i < this.points.length; i++) {
            this.points[i].setStart(this.points[i - 1].getEnd());
            this.initLine(this.points[i]);
            lengths.push(lengths[i - 1] + this.points[i].getLength());
        }

        //deter mine arrow position
        if (this.places != null && this.places.length > 0) {
            let repo = this.getDistributedArrowPlaces(lengths);
            for (let k in repo) {
                this.points[repo[k].key + 1].showArrow(this.direction, repo[k].items);
            }
        }
    }

    getDistributedArrowPlaces(lengths) {
        let max = lengths[lengths.length - 1];
        let p, temp;
        let repo = [];
        for (let i = 0; i < this.places.length; i++) {
            p = this.places[i] * max;
            for (let j = 0; j < lengths.length - 1; j++) {
                if (p >= lengths[j] && p <= lengths[j + 1]) {
                    temp = this.getRepoObject(repo, j);
                    temp.items.push((p - lengths[j]) / (lengths[j + 1] - lengths[j]));
                    break;
                }
            }
        }
        return repo;
    }

    getRepoObject(repo, key) {
        if (repo[key] == null)
            repo[key] = {key: key, items: [], places: []};
        return repo[key];
    }

    initLine(line) {
        line.setFillColor(this.fillColor);
        line.setStrokeColor(this.strokeColor);
        line.setStrokeWidth(this.strokeWidth);
        line.setStrokeStyle(this.strokeStyle);
    }
}
