import {Shape} from "../simple/shape";
import {Rectangle} from "../simple/rectangle";
import {Circle} from "../simple/circle";

export class Type extends Rectangle {
    type;
    r = 10;

    constructor() {
        super();
        this.setFillColor('#ffffe4');
        this.setStrokeColor('#cbcac6');
        this.setStrokeWidth(1);
        this.setHeight(this.r + 10)
    }

    setLabel(label) {
        if (typeof label === 'string') {
            label = new Text()
                .setPosition(this.getCenterPoint())
                .setOffset([this.r + 5, 0])
                .setLabel(label)
                .setColor('black')
        }
        return super.setLabel(label);
    }

    setType(type) {
        switch (type) {
            case 'class':
                this.type = this.getTagShape('C', '#659199')
                break;
            case 'interface':
                this.type = this.getTagShape('I', '#a1d383')
                break;
            case 'annotation':
                this.type = this.getTagShape('@', '#8ab472')
                break;
            default:
                break;
        }

        return this;
    }

    getTagShape(text, color) {
        return new Circle()
            .setPosition(this.getCenterPoint())
            .setOffset([-this.width + this.r + 2, 0])
            .setRadius(this.r)
            .setFillColor(color)
            .setLabel(text)
    }

    render(canvasCtx) {
        super.render(canvasCtx);
        if (this.type != null)
            this.type.render(canvasCtx);
    }
}
