import {Shape} from "./shape";
import {Utils} from "../../../tools/utils";
import {Text} from "./text";

export class Line extends Shape {
    start;
    end;
    qCurve;
    processed = false;

    constructor() {
        super();
        this.setStrokeWidth(1);
    }

    setPosition(position) {
        this.start = this.getPointObject(position);
        return this;
    }

    setStart(point) {
        return this.setPosition(point);
    }

    setEnd(point) {
        this.end = this.getPointObject(point);
        return this;
    }

    setQuadraticPoint(point) {
        this.qCurve = this.getPointObject(point);
        return this;
    }

    /**
     * @param point [x,y] or [intR, length] for radian, [intD, length] for degree or [shape,gap].<br/>
     */
    getPointObject(point) {
        let ans = {};
        switch (this.getTypeOf(point)) {
            case "number,number"://[x,y]
                ans["xy"] = point;
                break;
            case "string,number"://[intR,length] or [intD,length]
                let i = point[0].length - 1;
                let c = point[0][i];
                let v = Number(point[0].substring(0, i));
                if (c === 'R' || c === 'r') {
                    ans["angle"] = Utils.normalizeRadian(v);
                    ans["gap"] = point[1];
                } else if (c === 'D' || c === 'd') {
                    ans["angle"] = Utils.normalizeRadian(Utils.deg2Rad(v));
                    ans["gap"] = point[1];
                } else {
                    throw new Error('wrong input');
                }
                break;
            case "object,number"://[shape,gap]
                ans["shape"] = point[0];
                ans["gap"] = point[1];
                break;
            default:
                throw new Error('wrong input');
        }
        return ans;
    }

    getTypeOf(point) {
        return typeof point[0] + ',' + typeof point[1]
    }

    render(canvasCtx) {
        this.preparePoints();
        canvasCtx.beginPath();
        canvasCtx.moveTo(this.start[0], this.start[1]);
        if (this.qCurve)
            canvasCtx.quadraticCurveTo(this.qCurve[0], this.qCurve[1], this.end[0], this.end[1]);
        else
            canvasCtx.lineTo(this.end[0], this.end[1]);
        // canvasCtx.closePath();
        super.render(canvasCtx);
        //     if (this.direction !== undefined && this.places !== undefined) {
        //         if (!this.places[0])
        //             this.places = [this.places];
        //         let angle = this.direction ? Utils.getAngle(this.secondP[0], this.secondP[1], p1[0], p1[1]) : Utils.getAngle(p1[0], p1[1], this.secondP[0], this.secondP[1]);
        //         for (let i = 0; i < this.places.length; i++) {
        //             let x = p1[0] + (this.secondP[0] - p1[0]) * this.places[i];
        //             let y = p1[1] + (this.secondP[1] - p1[1]) * this.places[i];
        //             this.drawArrow(canvasCtx, 10, angle, x, y);
        //         }
        //     }
    }

    preparePoints() {
        debugger
        if (!this.processed) {
            if (this.qCurve != null) {
                if (this.qCurve["shape"] != null) {
                    this.process2PointsXY(this.qCurve, this.start, true);
                    this.process2PointsXY(this.qCurve, this.end, true);
                    let t = this.qCurve["shape"].getCenterPoint();
                    let angle1 = Utils.getAngleByPoint(t, this.start["xy"]);
                    let angle2 = Utils.getAngleByPoint(t, this.end["xy"]);
                    let midAngle = (angle1 + angle2) / 2;

                    if (Math.abs(angle1 - angle2) > Math.PI) //if angle1 - angle3 > 180deg => correct mid angle
                        midAngle -= Math.PI;
                    this.qCurve["xy"] = this.getTargetXY(this.qCurve["shape"], midAngle);
                } else {
                    this.process2PointsXY(this.start, this.qCurve);
                    this.process2PointsXY(this.qCurve, this.end);
                }
                this.start = this.start["xy"];
                this.end = this.end["xy"];
                this.qCurve = this.qCurve["xy"];
            } else {
                this.process2PointsXY(this.start, this.end);
                this.start = this.start["xy"];
                this.end = this.end["xy"];
            }
            this.processed = true;
        }
    }

    process2PointsXY(p1, p2, ignoreP1XYModification = false) {
        let angle;
        let c1 = p1["xy"] != null ? p1["xy"] : p1["shape"].getCenterPoint();
        if (p2["xy"] == null) {
            if (p2["angle"] != null) {
                angle = p2["angle"];
                p2["xy"] = Utils.getPointByAngle(c1, angle, p2["gap"]);
            } else if (p2["shape"] != null) {
                let c2 = p2["shape"].getCenterPoint();
                angle = Utils.getAngle(c2[0], c2[1], c1[0], c1[1]);//angle from p2 to p1
                p2["xy"] = this.getTargetXY(p2, angle);
            } else {
                throw new Error("no way to calculate point.xy");
            }
        }

        if (!ignoreP1XYModification && p1["xy"] == null) {
            // debugger
            // let d = Utils.rad2Deg(angle);
            // let d2 = Utils.normalizeDegree(d);
            //
            // let d3 = Utils.normalizeDegree(d - 180);
            // let d4 = Utils.normalizeDegree(d2 - 180);
            //
            // let d5 = Utils.rad2Deg();
            // let d6 = Utils.normalizeDegree(d5);
            // debugger
            angle = angle == null ? Utils.getAngleByPoint(c1, p2["xy"]) : angle - Math.PI;
            p1["xy"] = this.getTargetXY(p1, angle);
        }
    }

    //p must be a shape
    getTargetXY(p, angle) {
        return (p["shape"]["getExternalPoint"] != null) ?
            p["shape"].getExternalPoint(angle, p["gap"]) :
            Utils.getPointByAngle(p["shape"].getCenterPoint(), angle, p["gap"])
    }

    getCenterPoint() {
        this.preparePoints();
        return [(this.start[0] + this.end[0]) / 2, (this.start[1] + this.end[1]) / 2]
    }

    /**
     * show arrows on the line
     * @param direction 0 or 1
     * @param places array of float values, each is 0 - 1;
     * @return Shape
     */
    showArrow(direction, places) {
        this.direction = direction;
        this.places = places;
        return this;
    }

    drawArrow(canvasCtx, length, angle, x, y) {
        canvasCtx.beginPath();
        let p1 = [x + length * Math.cos(angle - Utils._PI6), y + length * Math.sin(angle - Utils._PI6)];
        let p2 = [x + length * Math.cos(angle + Utils._PI6), y + length * Math.sin(angle + Utils._PI6)];
        canvasCtx.moveTo(x, y);
        canvasCtx.lineTo(p1[0], p1[1]);
        canvasCtx.lineTo(p2[0], p2[1]);
        canvasCtx.closePath();
        super.render(canvasCtx);
    }
}

export class HLine
    extends Line {
    setLength(l) {
        l > 0 ?
            this.lineTo(['0D', l]) :
            this.lineTo(['180D', -l])
        return this;
    }
}

export class VLine extends Line {
    setLength(l) {
        l > 0 ?
            this.lineTo(['90D', l]) :
            this.lineTo(['270D', -l])
        return this;
    }
}

export class Vector extends Line {
    constructor() {
        super();
        this.setStrokeColor('black')
            .setFillColor('black')
            .setStrokeWidth(2)
            .showArrow(1, 1);
    }
}

export class HVector extends Vector {
    setLabel(label) {
        if (typeof label === 'string')
            label = new Text()
                .setPosition(this.getCenterPoint())
                .setLabel(label)
                .setOffset([0, -6])
                .setFontStyle('');
        return super.setLabel(label);
    }

    setLength(l) {
        return (l > 0) ?
            this.lineTo(['0D', l]) :
            this.lineTo(['180D', -l])
    }
}
