import {Rectangle} from "../simple/rectangle";
import {Text} from "../simple/text";
import {Line} from "../simple/line";

export class Caption extends Rectangle {
    constructor(handler) {
        super();
        this.handler = handler;
    }

    setLabel(label) {
        if (typeof label === 'string') {
            label = new Text()
                .setPosition(this.getCenterPoint())
                .setLabel(label)
                .setOffset([0, 5])
                .setFontStyle('bold');
        }
        return super.setLabel(label);
    }

    connectTo(refId) {
        this.drawBox.addShape(new Line()
            .between(this, this.handler.getTargetShape(refId), 5)
            .setStrokeColor('red')
            .setFillColor('red')
            .setStrokeWidth(1));
        return this;
    }
}
