sourcesContent:
import React from "react";
import {Bash, Frame, GenericCode, Java, Xml} from "../../../components/blocks";
import {InfoIcon} from "../../../components/bubble";
import {
    Blue, BlueBold, Bold, Bullet, FlexRow, Highlight, HorizontalLine, Important, Purple, Red
} from "../../../components/components";
import {Float} from "../../../components/float";
import appContext from '../../../res/images/ApplicationContext.png';
import circularDependency from "../../../res/images/circular-dependency.png";
import beanLifecycle from "../../../res/images/bean-life-cycle.png";
import components from "../../../res/images/components.png";
import {DrawBoxComponent} from "../../git/git-components";
import {DrawBoxWrapper} from "../../../components/drawbox/draw-box-wrapper";

export function CoreCheats() {
    return (
        <>
            {getBeanContainer()}
            {getScanningFilter()}
            {getMerging()}
            {getProfile()}
            {getMultipleCandidate()}
            {getXmlBasic()}
            {getAnnotationBasic()}
            {getAlias()}
            {getPrivateConstructor()}
            {getXmlLocalClass()}
            {getAnnotationLocalClass()}
            {getXmlCircularDependencies()}
            {getAnnotationCircularDependencies()}
            {getXmlLoadFromFile()}
            {getJavaLoadFromFile()}
            {getXmlChangingMethodBody()}
            {getCollections()}
            {getSpel()}
            {getScopes()}
            {getScopeIssue()}
            {getBeanAware()}
            {getTemplateClass()}
            {getEvent()}
            {getAsyncJob()}
        </>
    )
}

function getBeanContainer() {
    return (
        <Frame title={'Catching bean'}>
            <InfoIcon>
                <Important>!the path in which we put java source and resource files is called ClassPath. It also can
                    hold resources too.<br/>
                </Important>
                This is why, we use ClassPathXmlResource class to bring in our desired configuration file.<br/>
                Every thing under main dir is called ClassPath, so test is out of that.
            </InfoIcon>
            <FlexRow>
                <Java description={'using BeanFactory is deprecated'}>
                    {`
                    ClassPathResource resource = new ClassPathResource("containerconfig.xml");
                    BeanFactory beanFactory = new XmlBeanFactory(resource);
                    return beanFactory.getBean("targetId");
                    `}
                </Java>
                <Java description={'new fasion'}>
                    {`
                    ApplicationContext context = new ClassPathXmlApplicationContext("containerconfig.xml");
                    return (Car) context.getBean("car");`}
                </Java>
            </FlexRow>
            <Important>ApplicationContext provides multiple configuration file registration.</Important>
            <div className={'float-holder center-horizontally-relative inline'}>
                <DrawBoxComponent wh={[1200, 800]} shapeFactory={
                    new DrawBoxWrapper()
                        .addShape({shape: 'type', id: 'i1', label: 'ResourceLoader', t: 'i', pos: [40, 20], width: 125})
                        .addShape({
                            shape: 'type', id: 'i2', label: 'ResourcePatternResolver', t: 'i', align: 'i1',
                            offset: [-87.5, 60], width: 175
                        })
                        .addShape({
                            shape: 'type', id: 'i3', label: 'ListableBeanFactory', t: 'i', align: 'i2',
                            offset: [100, -12], width: 150
                        })
                        .addShape({
                            shape: 'type', id: 'i4', label: 'HierarchicalBeanFactory', t: 'i', align: 'i3',
                            offset: [87, -12], width: 168
                        })
                        .addShape({
                            shape: 'type', id: 'i5', label: 'MessageSource', t: 'i', align: 'i4', offset: [95, -12],
                            width: 125
                        })
                        .addShape({
                            shape: 'type', id: 'i6', label: 'EnvironmentCapable', t: 'i', align: 'i5',
                            offset: [75, -12], width: 150
                        })
                        .addShape({
                            shape: 'type', id: 'i7', label: 'ApplicationEventPublisher', t: 'i', align: 'i6',
                            offset: [87, -12], width: 175
                        })
                        .addShape({
                            shape: 'type', id: 'i8', label: 'BeanFactory', t: 'i', align: 'i1', offset: [200, -12],
                            width: 105
                        })
                        .addShape({
                            shape: 'type', id: 'i9', label: 'FunctionalInterface', t: '@', align: 'i8',
                            offset: [503, -12], width: 145
                        })
                        .addShape({
                            shape: 'type', id: 'i10', label: 'ApplicationContext', t: 'i', align: 'i4', offset: [0, 90],
                            width: 145
                        })
                        .addShape({
                            shape: 'type', id: 'c1', label: 'GenericApplicationContext', t: 'c', align: 'i10',
                            offset: [-304, 60], width: 180
                        })
                        .addShape({
                            shape: 'type', id: 'c2', label: 'FileSystemXmlApplicationContext', t: 'c', align: 'c1',
                            offset: [105, -12], width: 218
                        })
                        .addShape({
                            shape: 'type', id: 'c3', label: 'ClassPathXmlApplicationContext', t: 'c', align: 'c2',
                            offset: [123, -12], width: 215
                        })
                        .addShape({
                            shape: 'type', id: 'c4', label: 'StaticApplicationContext', t: 'c', align: 'c1',
                            offset: [-295, 60], width: 170
                        })
                        .addShape({
                            shape: 'type', id: 'c5', label: 'AnnotationConfigApplicationContext', t: 'c', align: 'c4',
                            offset: [95, -12], width: 230
                        })
                        .addShape({
                            shape: 'type', id: 'c6', label: 'GenericGroovyApplicationContext', t: 'c', align: 'c5',
                            offset: [123, -12], width: 220
                        })
                        .addShape({shape: 'typerel', t: 'ii', between: [['i2'], ['i1'], [20, '_']]})
                        .addShape({shape: 'typerel', t: 'ii', between: [['i3'], ['i8', -10, 0], [20, '_', -6]]})
                        .addShape({shape: 'typerel', t: 'ii', between: [['i4'], ['i8', 10, 0], [20, '_', -6]]})
                        .addShape({shape: 'typerel', t: 'i@', between: [['i7'], ['i9', 0, -1], [20, '_']]})
                        .addShape({shape: 'typerel', t: 'ii', between: [['i10', -25, 0], ['i2'], [20, '_']]})
                        .addShape({shape: 'typerel', t: 'ii', between: [['i10', -15, 0], ['i3'], [30, '_']]})
                        .addShape({shape: 'typerel', t: 'ii', between: [['i10', -5, 0], ['i4'], [40, '_']]})
