                        .addShape({shape: 'typerel', t: 'ci', between: [['c3'], ['i10', 20, 0], [20, '_', -10]]})
                        .addShape({shape: 'typerel', t: 'cc', between: [['c4'], ['c1', -20, 0], [20, '_', -11]]})
