export class DrawBox {
    lst = [];

    addShape(shape) {
        this.lst.push(shape);
        return this;
    }

    render(canvas) {
        let ctx = canvas.getContext('2d');
        this.lst.forEach(v => v.render(ctx));
        this.ctx = ctx;
        this.len = this.lst.length;
        this.finished = true;
        this.resumeRender();
    }

    resumeRender() {
        if (this.finished) {
            for (let i = this.len; i < this.lst.length; i++) {
                this.lst[i].render(this.ctx);
            }
        }
    }
}
